document.addEventListener('DOMContentLoaded',function(){const mobileMenuToggle=document.querySelector('.mobile-menu-toggle');const navLinks=document.querySelector('.nav-links');if(mobileMenuToggle&&navLinks){mobileMenuToggle.addEventListener('click',function(){navLinks.classList.toggle('active');this.classList.toggle('active');});}const fadeElements=document.querySelectorAll('.fade-in');const fadeInObserver=new IntersectionObserver((entries)=>{entries.forEach(entry=>{if(entry.isIntersecting){entry.target.classList.add('fade-in-visible');fadeInObserver.unobserve(entry.target);}});},{threshold:0.1});fadeElements.forEach(element=>{fadeInObserver.observe(element);});const contactForm=document.getElementById('contact-form');if(contactForm){contactForm.addEventListener('submit',function(e){e.preventDefault();if(validateForm()){console.log('Form is valid, submitting...');alert('Bedankt voor uw bericht. We nemen zo snel mogelijk contact met u op.');contactForm.reset();}});}console.log('MaasISO website loaded successfully');});function validateForm(){const name=document.getElementById('name');const email=document.getElementById('email');const phone=document.getElementById('phone');const subject=document.getElementById('subject');const message=document.getElementById('message');let isValid=true;clearErrors();if(name.value.trim()===''){showError(name,'Naam is verplicht');isValid=false;}if(email.value.trim()===''){showError(email,'E-mail is verplicht');isValid=false;}else if(!isValidEmail(email.value)){showError(email,'Voer een geldig e-mailadres in');isValid=false;}if(phone.value.trim()!==''&&!isValidPhone(phone.value)){showError(phone,'Voer een geldig telefoonnummer in');isValid=false;}if(subject.value===''){showError(subject,'Kies een onderwerp');isValid=false;}if(message.value.trim()===''){showError(message,'Bericht is verplicht');isValid=false;}return isValid;}function showError(input,message){const formGroup=input.closest('.form-group');const error=document.createElement('div');error.className='error-message';error.textContent=message;formGroup.appendChild(error);input.classList.add('invalid');}function clearErrors(){const errorMessages=document.querySelectorAll('.error-message');errorMessages.forEach(error=>error.remove());const invalidInputs=document.querySelectorAll('.invalid');invalidInputs.forEach(input=>input.classList.remove('invalid'));}function isValidEmail(email){const re=/^[^\s@]+@[^\s@]+\.[^\s@]+$/;return re.test(email);}function isValidPhone(phone){const re=/^[+]?[(]?[0-9]{3}[)]?[-\s.]?[0-9]{3}[-\s.]?[0-9]{4,6}$/;return re.test(phone);}
